#!/usr/bin/python

import random

TOTAL_NRO_LINES=10000000
VERSIONS_FACTOR=8
UPPERBOUND=TOTAL_NRO_LINES/VERSIONS_FACTOR

print('*** Generating input file...')

randfile = open("data/input/big.csv", "w" )

for i in range(TOTAL_NRO_LINES):
    if i % 10000 == 0:
        print('Generating ' + str(i) + ' / ' + str(TOTAL_NRO_LINES) + '...')
    line = str(random.randint(1, UPPERBOUND)) + "," + str(random.randint(1,100)) + "," + "\n"
    randfile.write(line)

randfile.close()

print('*** Done')



